mapper.update:
update USER_INFO
set IMAGE = #{image}
where EMAIL = #{email}

mapper.select:
SELECT '자유' AS BOARD_TYPE, TITLE, CREATED_DATE FROM FREE_BOARD WHERE EMAIL = #{email}
UNION ALL
SELECT '제보' AS BOARD_TYPE, TITLE, CREATED_DATE FROM REPORT_BOARD WHERE EMAIL = #{email}
ORDER BY CREATED_DATE DESC
